function setup(){console.log("Loaded.");drawGrid();drawLine();}
var maxNumberY=1000;var ySteps=10;function increaseNumberY(){if(maxNumberY==2200)return
maxNumberY+=100;ySteps+=1;redraw();}
function decreaseNumberY(){if(maxNumberY==300)return
maxNumberY-=100;ySteps-=1;redraw();}
function redraw(){const context=canvas.getContext('2d');context.clearRect(0,0,canvas.width,canvas.height);drawGrid();drawLine();}
function mapValueToPixel(value,valueMin,valueMax,pixelMin,pixelMax){const ratio=(pixelMax-pixelMin)/(valueMax-valueMin);const pixel=(value-valueMin)*ratio+pixelMin;return pixel;}
function drawLine(){const canvas=document.getElementById("canvas");const ctx=canvas.getContext("2d");const data=[100,200,5,15,5,12,8,14,3,6,10,13]
var factor=30;ctx.beginPath();ctx.lineWidth=3;for(var i=0;i<data.length+1;i++){if(i==0){yAxis=mapValueToPixel(data[0],maxNumberY,0,30,580)
ctx.moveTo(factor,yAxis);}else{factor+=750/12;yAxis_end=mapValueToPixel(data[i],maxNumberY,0,30,580)
ctx.lineTo(factor,yAxis_end);}}
ctx.stroke();ctx.lineWidth=1;factor=30;ctx.beginPath();yAxis=mapValueToPixel(data[0],maxNumberY,0,30,580);ctx.arc(factor,yAxis,7,0,2*Math.PI);ctx.fillStyle='red';ctx.fill();ctx.stroke();for(var i=0;i<data.length;i++){if(i==0){factor=30;}else{factor+=750/12;ctx.beginPath();yAxis=mapValueToPixel(data[i],maxNumberY,0,30,580);ctx.arc(factor,yAxis,7,0,2*Math.PI);ctx.fillStyle='red';ctx.fill();ctx.stroke();}}}
function drawGrid(){const canvas=document.getElementById("canvas");const ctx=canvas.getContext("2d");ctx.beginPath();ctx.moveTo(30,30);ctx.lineTo(30,580);ctx.stroke();ctx.beginPath();ctx.moveTo(30,580);ctx.lineTo(750,580);ctx.stroke();var step=30;var number=maxNumberY;for(var i=0;i<=ySteps;i++){ctx.beginPath();ctx.moveTo(25,step);ctx.lineTo(30,step);ctx.stroke();ctx.font="12px Arial";ctx.fillText(number,0,step);step+=(550/ySteps);number-=100;}
var month_step=30;var months=['jan','feb','mar','apr','may','jun','jul','aug','sep','oct','nov','dec'];for(var i=0;i<12;i++){ctx.beginPath();ctx.moveTo(month_step,580);ctx.lineTo(month_step,590);ctx.stroke();ctx.font="12px Arial";ctx.fillText(months[i],month_step-5,600);month_step+=750/12;}
var step=30;var number=maxNumberY;ctx.lineWidth=.5;for(var i=0;i<=ySteps;i++){ctx.beginPath();ctx.moveTo(30,step);ctx.lineTo(750,step);ctx.stroke();step+=(550/(ySteps));}
var month_step=30;for(var i=0;i<12;i++){ctx.beginPath();ctx.moveTo(month_step,30);ctx.lineTo(month_step,580);ctx.stroke();month_step+=750/12;}
ctx.lineWidth=1;}